#!/usr/bin/env bash
set -e

# Requires tor to be running on the local machine
collat_witness() {
  # Inputs: $1 = tx_cbor, $2 = network (preprod or mainnet)
  local tx_cbor=$1
  local network=$2

  # Tor hidden service URL (replace with your actual .onion URL)
  local tor_url="fjy3v62j7vqytvtviixsbixcmgyxgfolb7pg5bb3vcozxn4rrlu7z6ad.onion"

  # Perform the curl request through the Tor network
  local response=$(curl -s --socks5-hostname 127.0.0.1:9050 -X POST "http://${tor_url}/${network}/collateral/" \
    -H 'Content-Type: application/json' \
    -d '{
          "tx_body": "'"${tx_cbor}"'"
        }')

  # Check if the response contains a 'witness' field
  local collat_witness=$(echo "$response" | jq -r '.witness')

  # If the witness is present, return it, otherwise handle the error
  if [[ "$collat_witness" != "null" && -n "$collat_witness" ]]; then
    echo "$collat_witness"
  else
    # Optionally, capture the error message from the response
    local error_message=$(echo "$response")
    echo -e "\033[0;31m$error_message\033[0m"
    exit 1
  fi
}


# Example Usage

tx_cbor=""
collat_witness "$tx_cbor" "preprod"